CREATE TABLE IF NOT EXISTS mail_for_sending (  id serial NOT NULL ,  date_time timestamp DEFAULT NULL,  from_addr varchar(50) NOT NULL,  from_name varchar(255) DEFAULT NULL,    to_addr varchar(50) NOT NULL,  to_name varchar(255) DEFAULT NULL,  reply_addr varchar(50) DEFAULT NULL,  reply_name varchar(255) DEFAULT NULL,    body text DEFAULT NULL,  subject varchar(255) DEFAULT NULL,  sender_addr varchar(50) DEFAULT NULL,  sent boolean NOT NULL DEFAULT false,    CONSTRAINT mail_for_sending_pkey PRIMARY KEY (id));ALTER TABLE mail_for_sending OWNER TO polimerplast;CREATE INDEX mail_for_sending_date_time_index  ON mail_for_sending USING btree (date_time);CREATE INDEX mail_for_sending_sent_index  ON mail_for_sending USING btree (sent);  /*Attachments*/  CREATE TABLE IF NOT EXISTS mail_for_sending_attachments (  id serial NOT NULL ,  mail_for_sending_id integer REFERENCES mail_for_sending (id),  file_name varchar(255) NOT NULL,  CONSTRAINT mail_for_sending_attachments_pkey PRIMARY KEY (id));ALTER TABLE mail_for_sending_attachments OWNER TO polimerplast;CREATE INDEX mail_for_sending_attachments_id_index ON mail_for_sending_attachments USING btree (mail_for_sending_id);  